        //Adding Color
        var $color = UIUtils.getUnselectableGenericElement("li", "", "color");
        $color.addClass("c-" + clr);
        $color.addClass("i-" + i);
        $color
            .attr('clr', clr)
            .attr('i', i);

        var $kid = UIUtils.getUnselectableGenericElement("div", "", "kid");

        if (clr === "NoColor") {
            $kid.text("No Color");
        }
        else {
            $kid.css({ "background-color": "#" + clr });
        }

        $color.append($kid);
        $color.attr("tabindex", "0");
        return $color;
