function getRadioButton(options) {
    var $radio = UIUtils.getGenericHtmlElement({
        tagName: "input",
        id: options.id,
        class: options.className,
        attributes: [{
            name: "name",
            value: options.name
        }, {
            name: "type",
            value: 'radio'
        }]
    });

    var $div = $("<span/>");
    $div.addClass(options.class);
    $div.text(options.text);
    $div.prepend($radio);
    
    return $div;
};

